Theorem partition_'vc (m : Z) (n : Z) (a : list Z) (f : Z) (fact0 : 0%Z ≤ m) (fact1 : m < n) (fact2 : n < Z.of_nat (length a)) (fact3 : m ≤ f) (fact4 : f ≤ n) : 0%Z ≤ f ∧ f < Z.of_nat (length a) ∧ (let x : Z := nth (Z.to_nat f) a inhabitant in (∀(j : Z) (i : Z) (a1 : list Z), length a1 = length a -> ((m ≤ j ∧ j ≤ n) ∧ m ≤ i ∧ i ≤ n) ∧ permut_sub' a a1 (Z.to_nat m) (Z.to_nat (n + 1%Z)) ∧ (∀(r : Z), m ≤ r ∧ r < i -> nth (Z.to_nat r) a1 inhabitant ≤ x) ∧ (∀(r : Z), j < r ∧ r ≤ n -> x ≤ nth (Z.to_nat r) a1 inhabitant) ∧ nth (Z.to_nat f) a1 inhabitant = x -> ((m ≤ i ∧ i ≤ n) ∧ (∀(r : Z), m ≤ r ∧ r < i -> nth (Z.to_nat r) a1 inhabitant ≤ x)) ∧ (∀(i1 : Z), (m ≤ i1 ∧ i1 ≤ n) ∧ (∀(r : Z), m ≤ r ∧ r < i1 -> nth (Z.to_nat r) a1 inhabitant ≤ x) -> (i1 < n -> 0%Z ≤ i1 ∧ i1 < Z.of_nat (length a1)) ∧ (∀(o1 : bool), (if decide (i1 < n) then o1 = (if decide (nth (Z.to_nat i1) a1 inhabitant ≤ x) then true else false) else o1 = false) -> (if decide (o1 = true) then (0%Z ≤ n - i1 ∧ n - (i1 + 1%Z) < n - i1) ∧ (m ≤ i1 + 1%Z ∧ i1 + 1%Z ≤ n) ∧ (∀(r : Z), m ≤ r ∧ r < i1 + 1%Z -> nth (Z.to_nat r) a1 inhabitant ≤ x) else ((m ≤ j ∧ j ≤ n) ∧ (∀(r : Z), j < r ∧ r ≤ n -> x ≤ nth (Z.to_nat r) a1 inhabitant)) ∧ (∀(j1 : Z), (m ≤ j1 ∧ j1 ≤ n) ∧ (∀(r : Z), j1 < r ∧ r ≤ n -> x ≤ nth (Z.to_nat r) a1 inhabitant) -> (m < j1 -> 0%Z ≤ j1 ∧ j1 < Z.of_nat (length a1)) ∧ (∀(o2 : bool), (if decide (m < j1) then o2 = (if decide (x ≤ nth (Z.to_nat j1) a1 inhabitant) then true else false) else o2 = false) -> (if decide (o2 = true) then (0%Z ≤ j1 ∧ j1 - 1%Z < j1) ∧ (m ≤ j1 - 1%Z ∧ j1 - 1%Z ≤ n) ∧ (∀(r : Z), j1 - 1%Z < r ∧ r ≤ n -> x ≤ nth (Z.to_nat r) a1 inhabitant) else if decide (i1 < j1) then ((0%Z ≤ m ∧ m ≤ i1 ∧ i1 ≤ n ∧ n < Z.of_nat (length a1)) ∧ m ≤ j1 ∧ j1 ≤ n) ∧ (∀(a2 : list Z), length a2 = length a1 -> array_exchange a1 a2 i1 j1 ∧ permut_sub' a1 a2 (Z.to_nat m) (Z.to_nat (n + 1%Z)) -> ((0%Z ≤ 1%Z + j - i ∧ 1%Z + (j1 - 1%Z) - (i1 + 1%Z) < 1%Z + j - i) ∧ ((m ≤ j1 - 1%Z ∧ j1 - 1%Z ≤ n) ∧ m ≤ i1 + 1%Z ∧ i1 + 1%Z ≤ n) ∧ permut_sub' a a2 (Z.to_nat m) (Z.to_nat (n + 1%Z)) ∧ (∀(r : Z), m ≤ r ∧ r < i1 + 1%Z -> nth (Z.to_nat r) a2 inhabitant ≤ x) ∧ (∀(r : Z), j1 - 1%Z < r ∧ r ≤ n -> x ≤ nth (Z.to_nat r) a2 inhabitant) ∧ nth (Z.to_nat f) a2 inhabitant = x) ∧ (∀(j2 : Z) (i2 : Z) (a3 : list Z), length a3 = length a2 -> (m ≤ j2 ∧ j2 ≤ i2 ∧ i2 ≤ n) ∧ permut_sub' a2 a3 (Z.to_nat m) (Z.to_nat (n + 1%Z)) ∧ (i2 = n ∨ x < nth (Z.to_nat i2) a3 inhabitant) ∧ (j2 = m ∨ nth (Z.to_nat j2) a3 inhabitant < x) ∧ nth (Z.to_nat f) a3 inhabitant = x ∧ (∀(r : Z), m ≤ r ∧ r < i2 -> nth (Z.to_nat r) a3 inhabitant ≤ x) ∧ (∀(r : Z), j2 < r ∧ r ≤ n -> x ≤ nth (Z.to_nat r) a3 inhabitant) -> (m ≤ j2 ∧ j2 ≤ i2 ∧ i2 ≤ n) ∧ permut_sub' a1 a3 (Z.to_nat m) (Z.to_nat (n + 1%Z)) ∧ (i2 = n ∨ x < nth (Z.to_nat i2) a3 inhabitant) ∧ (j2 = m ∨ nth (Z.to_nat j2) a3 inhabitant < x) ∧ nth (Z.to_nat f) a3 inhabitant = x ∧ (∀(r : Z), m ≤ r ∧ r < i2 -> nth (Z.to_nat r) a3 inhabitant ≤ x) ∧ (∀(r : Z), j2 < r ∧ r ≤ n -> x ≤ nth (Z.to_nat r) a3 inhabitant))) else (m ≤ j1 ∧ j1 ≤ i1 ∧ i1 ≤ n) ∧ permut_sub' a1 a1 (Z.to_nat m) (Z.to_nat (n + 1%Z)) ∧ (i1 = n ∨ x < nth (Z.to_nat i1) a1 inhabitant) ∧ (j1 = m ∨ nth (Z.to_nat j1) a1 inhabitant < x) ∧ nth (Z.to_nat f) a1 inhabitant = x ∧ (∀(r : Z), m ≤ r ∧ r < i1 -> nth (Z.to_nat r) a1 inhabitant ≤ x) ∧ (∀(r : Z), j1 < r ∧ r ≤ n -> x ≤ nth (Z.to_nat r) a1 inhabitant)))))))) ∧ (((m ≤ n ∧ n ≤ n) ∧ m ≤ m ∧ m ≤ n) ∧ permut_sub' a a (Z.to_nat m) (Z.to_nat (n + 1%Z)) ∧ (∀(r : Z), m ≤ r ∧ r < m -> nth (Z.to_nat r) a inhabitant ≤ x) ∧ (∀(r : Z), n < r ∧ r ≤ n -> x ≤ nth (Z.to_nat r) a inhabitant) ∧ nth (Z.to_nat f) a inhabitant = x) ∧ (∀(j : Z) (i : Z) (a1 : list Z), length a1 = length a -> (m ≤ j ∧ j ≤ i ∧ i ≤ n) ∧ permut_sub' a a1 (Z.to_nat m) (Z.to_nat (n + 1%Z)) ∧ (i = n ∨ x < nth (Z.to_nat i) a1 inhabitant) ∧ (j = m ∨ nth (Z.to_nat j) a1 inhabitant < x) ∧ nth (Z.to_nat f) a1 inhabitant = x ∧ (∀(r : Z), m ≤ r ∧ r < i -> nth (Z.to_nat r) a1 inhabitant ≤ x) ∧ (∀(r : Z), j < r ∧ r ≤ n -> x ≤ nth (Z.to_nat r) a1 inhabitant) -> (if decide (i < f) then ((0%Z ≤ m ∧ m ≤ i ∧ i ≤ n ∧ n < Z.of_nat (length a1)) ∧ m ≤ f ∧ f ≤ n) ∧ (∀(a2 : list Z), length a2 = length a1 -> array_exchange a1 a2 i f ∧ permut_sub' a1 a2 (Z.to_nat m) (Z.to_nat (n + 1%Z)) -> (m ≤ j ∧ j < i + 1%Z ∧ i + 1%Z ≤ n) ∧ permut_sub' a a2 (Z.to_nat m) (Z.to_nat (n + 1%Z)) ∧ (∀(r : Z), m ≤ r ∧ r ≤ j -> nth (Z.to_nat r) a2 inhabitant ≤ x) ∧ (∀(r : Z), j < r ∧ r < i + 1%Z -> nth (Z.to_nat r) a2 inhabitant = x) ∧ (∀(r : Z), i + 1%Z ≤ r ∧ r ≤ n -> x ≤ nth (Z.to_nat r) a2 inhabitant)) else if decide (f < j) then ((0%Z ≤ m ∧ m ≤ f ∧ f ≤ n ∧ n < Z.of_nat (length a1)) ∧ m ≤ j ∧ j ≤ n) ∧ (∀(a2 : list Z), length a2 = length a1 -> array_exchange a1 a2 f j ∧ permut_sub' a1 a2 (Z.to_nat m) (Z.to_nat (n + 1%Z)) -> (m ≤ j - 1%Z ∧ j - 1%Z < i ∧ i ≤ n) ∧ permut_sub' a a2 (Z.to_nat m) (Z.to_nat (n + 1%Z)) ∧ (∀(r : Z), m ≤ r ∧ r ≤ j - 1%Z -> nth (Z.to_nat r) a2 inhabitant ≤ x) ∧ (∀(r : Z), j - 1%Z < r ∧ r < i -> nth (Z.to_nat r) a2 inhabitant = x) ∧ (∀(r : Z), i ≤ r ∧ r ≤ n -> x ≤ nth (Z.to_nat r) a2 inhabitant)) else (m ≤ j ∧ j < i ∧ i ≤ n) ∧ permut_sub' a a1 (Z.to_nat m) (Z.to_nat (n + 1%Z)) ∧ (∀(r : Z), m ≤ r ∧ r ≤ j -> nth (Z.to_nat r) a1 inhabitant ≤ x) ∧ (∀(r : Z), j < r ∧ r < i -> nth (Z.to_nat r) a1 inhabitant = x) ∧ (∀(r : Z), i ≤ r ∧ r ≤ n -> x ≤ nth (Z.to_nat r) a1 inhabitant)))).
Proof.
Admitted.
