Theorem quick_rec'vc (l : Z) (r : Z) (a : list Z) (fact0 : 0%Z ≤ l) (fact1 : l ≤ r) (fact2 : r ≤ Z.of_nat (length a)) : if decide (r ≤ l + 1%Z) then sorted_sub1 a l r ∧ permut_sub' a a (Z.to_nat l) (Z.to_nat r) else (0%Z ≤ l ∧ l < Z.of_nat (length a)) ∧ (let v : Z := nth (Z.to_nat l) a inhabitant in ((l ≤ l ∧ l < r - 1%Z) ∧ (∀(k : Z), l ≤ k ∧ k ≤ l -> nth (Z.to_nat k) a inhabitant ≤ v) ∧ (l < r ∧ r ≤ r) ∧ (∀(k : Z), r ≤ k ∧ k < r -> v ≤ nth (Z.to_nat k) a inhabitant) ∧ permut_sub' a a (Z.to_nat l) (Z.to_nat r) ∧ nth (Z.to_nat l) a inhabitant = v) ∧ (∀(j : Z) (i : Z) (a1 : list Z), length a1 = length a -> (l ≤ i ∧ i < r - 1%Z) ∧ (∀(k : Z), l ≤ k ∧ k ≤ i -> nth (Z.to_nat k) a1 inhabitant ≤ v) ∧ (l < j ∧ j ≤ r) ∧ (∀(k : Z), j ≤ k ∧ k < r -> v ≤ nth (Z.to_nat k) a1 inhabitant) ∧ permut_sub' a a1 (Z.to_nat l) (Z.to_nat r) ∧ nth (Z.to_nat l) a1 inhabitant = v -> ((i < i + 1%Z ∧ i + 1%Z < r) ∧ (∀(k : Z), l ≤ k ∧ k < i + 1%Z -> nth (Z.to_nat k) a1 inhabitant ≤ v)) ∧ (∀(i1 : Z), (i < i1 ∧ i1 < r) ∧ (∀(k : Z), l ≤ k ∧ k < i1 -> nth (Z.to_nat k) a1 inhabitant ≤ v) -> (0%Z ≤ i1 ∧ i1 < Z.of_nat (length a1)) ∧ (if decide (nth (Z.to_nat i1) a1 inhabitant < v) then if decide (i1 = r - 1%Z) then ((l ≤ j - 1%Z ∧ j - 1%Z < j) ∧ (∀(k : Z), j - 1%Z < k ∧ k < r -> v ≤ nth (Z.to_nat k) a1 inhabitant)) ∧ (∀(j1 : Z), (l ≤ j1 ∧ j1 < j) ∧ (∀(k : Z), j1 < k ∧ k < r -> v ≤ nth (Z.to_nat k) a1 inhabitant) -> (0%Z ≤ j1 ∧ j1 < Z.of_nat (length a1)) ∧ (if decide (v < nth (Z.to_nat j1) a1 inhabitant) then if decide (j1 = l) then if decide (j1 ≤ i1) then ((0%Z ≤ l ∧ l < Z.of_nat (length a1)) ∧ 0%Z ≤ j1 ∧ j1 < Z.of_nat (length a1)) ∧ (∀(a2 : list Z), length a2 = length a1 -> array_exchange a1 a2 l j1 -> ((0%Z ≤ r - l ∧ j1 - l < r - l) ∧ 0%Z ≤ l ∧ l ≤ j1 ∧ j1 ≤ Z.of_nat (length a2)) ∧ (∀(a3 : list Z), length a3 = length a2 -> sorted_sub1 a3 l j1 ∧ permut_sub' a2 a3 (Z.to_nat l) (Z.to_nat j1) -> (let o1 : Z := j1 + 1%Z in ((0%Z ≤ r - l ∧ r - o1 < r - l) ∧ 0%Z ≤ o1 ∧ o1 ≤ r ∧ r ≤ Z.of_nat (length a3)) ∧ (∀(a4 : list Z), length a4 = length a3 -> sorted_sub1 a4 o1 r ∧ permut_sub' a3 a4 (Z.to_nat o1) (Z.to_nat r) -> sorted_sub1 a4 l r ∧ permut_sub' a a4 (Z.to_nat l) (Z.to_nat r))))) else ((0%Z ≤ i1 ∧ i1 < Z.of_nat (length a1)) ∧ 0%Z ≤ j1 ∧ j1 < Z.of_nat (length a1)) ∧ (∀(a2 : list Z), length a2 = length a1 -> array_exchange a1 a2 i1 j1 -> (0%Z ≤ j - i ∧ j1 - i1 < j - i) ∧ (l ≤ i1 ∧ i1 < r - 1%Z) ∧ (∀(k : Z), l ≤ k ∧ k ≤ i1 -> nth (Z.to_nat k) a2 inhabitant ≤ v) ∧ (l < j1 ∧ j1 ≤ r) ∧ (∀(k : Z), j1 ≤ k ∧ k < r -> v ≤ nth (Z.to_nat k) a2 inhabitant) ∧ permut_sub' a a2 (Z.to_nat l) (Z.to_nat r) ∧ nth (Z.to_nat l) a2 inhabitant = v) else (0%Z ≤ j1 ∧ j1 - 1%Z < j1) ∧ (l ≤ j1 - 1%Z ∧ j1 - 1%Z < j) ∧ (∀(k : Z), j1 - 1%Z < k ∧ k < r -> v ≤ nth (Z.to_nat k) a1 inhabitant) else if decide (j1 ≤ i1) then ((0%Z ≤ l ∧ l < Z.of_nat (length a1)) ∧ 0%Z ≤ j1 ∧ j1 < Z.of_nat (length a1)) ∧ (∀(a2 : list Z), length a2 = length a1 -> array_exchange a1 a2 l j1 -> ((0%Z ≤ r - l ∧ j1 - l < r - l) ∧ 0%Z ≤ l ∧ l ≤ j1 ∧ j1 ≤ Z.of_nat (length a2)) ∧ (∀(a3 : list Z), length a3 = length a2 -> sorted_sub1 a3 l j1 ∧ permut_sub' a2 a3 (Z.to_nat l) (Z.to_nat j1) -> (let o1 : Z := j1 + 1%Z in ((0%Z ≤ r - l ∧ r - o1 < r - l) ∧ 0%Z ≤ o1 ∧ o1 ≤ r ∧ r ≤ Z.of_nat (length a3)) ∧ (∀(a4 : list Z), length a4 = length a3 -> sorted_sub1 a4 o1 r ∧ permut_sub' a3 a4 (Z.to_nat o1) (Z.to_nat r) -> sorted_sub1 a4 l r ∧ permut_sub' a a4 (Z.to_nat l) (Z.to_nat r))))) else ((0%Z ≤ i1 ∧ i1 < Z.of_nat (length a1)) ∧ 0%Z ≤ j1 ∧ j1 < Z.of_nat (length a1)) ∧ (∀(a2 : list Z), length a2 = length a1 -> array_exchange a1 a2 i1 j1 -> (0%Z ≤ j - i ∧ j1 - i1 < j - i) ∧ (l ≤ i1 ∧ i1 < r - 1%Z) ∧ (∀(k : Z), l ≤ k ∧ k ≤ i1 -> nth (Z.to_nat k) a2 inhabitant ≤ v) ∧ (l < j1 ∧ j1 ≤ r) ∧ (∀(k : Z), j1 ≤ k ∧ k < r -> v ≤ nth (Z.to_nat k) a2 inhabitant) ∧ permut_sub' a a2 (Z.to_nat l) (Z.to_nat r) ∧ nth (Z.to_nat l) a2 inhabitant = v))) else (0%Z ≤ r - i1 ∧ r - (i1 + 1%Z) < r - i1) ∧ (i < i1 + 1%Z ∧ i1 + 1%Z < r) ∧ (∀(k : Z), l ≤ k ∧ k < i1 + 1%Z -> nth (Z.to_nat k) a1 inhabitant ≤ v) else ((l ≤ j - 1%Z ∧ j - 1%Z < j) ∧ (∀(k : Z), j - 1%Z < k ∧ k < r -> v ≤ nth (Z.to_nat k) a1 inhabitant)) ∧ (∀(j1 : Z), (l ≤ j1 ∧ j1 < j) ∧ (∀(k : Z), j1 < k ∧ k < r -> v ≤ nth (Z.to_nat k) a1 inhabitant) -> (0%Z ≤ j1 ∧ j1 < Z.of_nat (length a1)) ∧ (if decide (v < nth (Z.to_nat j1) a1 inhabitant) then if decide (j1 = l) then if decide (j1 ≤ i1) then ((0%Z ≤ l ∧ l < Z.of_nat (length a1)) ∧ 0%Z ≤ j1 ∧ j1 < Z.of_nat (length a1)) ∧ (∀(a2 : list Z), length a2 = length a1 -> array_exchange a1 a2 l j1 -> ((0%Z ≤ r - l ∧ j1 - l < r - l) ∧ 0%Z ≤ l ∧ l ≤ j1 ∧ j1 ≤ Z.of_nat (length a2)) ∧ (∀(a3 : list Z), length a3 = length a2 -> sorted_sub1 a3 l j1 ∧ permut_sub' a2 a3 (Z.to_nat l) (Z.to_nat j1) -> (let o1 : Z := j1 + 1%Z in ((0%Z ≤ r - l ∧ r - o1 < r - l) ∧ 0%Z ≤ o1 ∧ o1 ≤ r ∧ r ≤ Z.of_nat (length a3)) ∧ (∀(a4 : list Z), length a4 = length a3 -> sorted_sub1 a4 o1 r ∧ permut_sub' a3 a4 (Z.to_nat o1) (Z.to_nat r) -> sorted_sub1 a4 l r ∧ permut_sub' a a4 (Z.to_nat l) (Z.to_nat r))))) else ((0%Z ≤ i1 ∧ i1 < Z.of_nat (length a1)) ∧ 0%Z ≤ j1 ∧ j1 < Z.of_nat (length a1)) ∧ (∀(a2 : list Z), length a2 = length a1 -> array_exchange a1 a2 i1 j1 -> (0%Z ≤ j - i ∧ j1 - i1 < j - i) ∧ (l ≤ i1 ∧ i1 < r - 1%Z) ∧ (∀(k : Z), l ≤ k ∧ k ≤ i1 -> nth (Z.to_nat k) a2 inhabitant ≤ v) ∧ (l < j1 ∧ j1 ≤ r) ∧ (∀(k : Z), j1 ≤ k ∧ k < r -> v ≤ nth (Z.to_nat k) a2 inhabitant) ∧ permut_sub' a a2 (Z.to_nat l) (Z.to_nat r) ∧ nth (Z.to_nat l) a2 inhabitant = v) else (0%Z ≤ j1 ∧ j1 - 1%Z < j1) ∧ (l ≤ j1 - 1%Z ∧ j1 - 1%Z < j) ∧ (∀(k : Z), j1 - 1%Z < k ∧ k < r -> v ≤ nth (Z.to_nat k) a1 inhabitant) else if decide (j1 ≤ i1) then ((0%Z ≤ l ∧ l < Z.of_nat (length a1)) ∧ 0%Z ≤ j1 ∧ j1 < Z.of_nat (length a1)) ∧ (∀(a2 : list Z), length a2 = length a1 -> array_exchange a1 a2 l j1 -> ((0%Z ≤ r - l ∧ j1 - l < r - l) ∧ 0%Z ≤ l ∧ l ≤ j1 ∧ j1 ≤ Z.of_nat (length a2)) ∧ (∀(a3 : list Z), length a3 = length a2 -> sorted_sub1 a3 l j1 ∧ permut_sub' a2 a3 (Z.to_nat l) (Z.to_nat j1) -> (let o1 : Z := j1 + 1%Z in ((0%Z ≤ r - l ∧ r - o1 < r - l) ∧ 0%Z ≤ o1 ∧ o1 ≤ r ∧ r ≤ Z.of_nat (length a3)) ∧ (∀(a4 : list Z), length a4 = length a3 -> sorted_sub1 a4 o1 r ∧ permut_sub' a3 a4 (Z.to_nat o1) (Z.to_nat r) -> sorted_sub1 a4 l r ∧ permut_sub' a a4 (Z.to_nat l) (Z.to_nat r))))) else ((0%Z ≤ i1 ∧ i1 < Z.of_nat (length a1)) ∧ 0%Z ≤ j1 ∧ j1 < Z.of_nat (length a1)) ∧ (∀(a2 : list Z), length a2 = length a1 -> array_exchange a1 a2 i1 j1 -> (0%Z ≤ j - i ∧ j1 - i1 < j - i) ∧ (l ≤ i1 ∧ i1 < r - 1%Z) ∧ (∀(k : Z), l ≤ k ∧ k ≤ i1 -> nth (Z.to_nat k) a2 inhabitant ≤ v) ∧ (l < j1 ∧ j1 ≤ r) ∧ (∀(k : Z), j1 ≤ k ∧ k < r -> v ≤ nth (Z.to_nat k) a2 inhabitant) ∧ permut_sub' a a2 (Z.to_nat l) (Z.to_nat r) ∧ nth (Z.to_nat l) a2 inhabitant = v))))))).
Proof.
Admitted.
