Axiom mixfix_lbrb_closure : forall {α : Type} `{Inhabited α}, list α -> Z -> α.
Axiom mixfix_lbrb_closure_def : forall  {α : Type} `{Inhabited α} (y : list α) (y1 : Z), mixfix_lbrb_closure y y1 = nth (Z.to_nat y1) y inhabitant.
Definition array_sum (a : list Z) (l : Z) (h : Z) : Z := sum (mixfix_lbrb_closure a) l h.
Definition col (m : matrix Z) (j : Z) (i : Z) : Z := elts m i j.
Axiom col_closure : matrix Z -> Z -> Z -> Z.
Axiom fc : matrix Z -> Z -> Z -> Z -> Z.
Axiom col_closure_def : forall  (y : matrix Z) (y1 : Z) (y2 : Z), col_closure y y1 y2 = col y y1 y2.
Axiom fc'def : forall  (m : matrix Z) (rl : Z) (rh : Z) (j : Z), fc m rl rh j = sum (col_closure m j) rl rh.
Definition matrix_sum (m : matrix Z) (rl : Z) (rh : Z) (cl : Z) (ch : Z) : Z := sum (fc m rl rh) cl ch.
Theorem maximum_submatrix'vc (m : matrix Z) : let o1 : Z := columns m in 0%Z ≤ o1 ∧ (∀(a : list Z), (∀(i : Z), 0%Z ≤ i ∧ i < o1 -> nth (Z.to_nat i) a inhabitant = 0%Z) ∧ Z.of_nat (length a) = o1 -> (let o2 : Z := rows m - 1%Z in (0%Z ≤ o2 + 1%Z -> ((0%Z ≤ 0%Z ∧ 0%Z ≤ 0%Z ∧ 0%Z ≤ rows m) ∧ (0%Z ≤ 0%Z ∧ 0%Z ≤ 0%Z ∧ 0%Z ≤ columns m) ∧ (0%Z = matrix_sum m 0%Z 0%Z 0%Z 0%Z ∧ 0%Z ≤ matrix_sum m 0%Z 0%Z 0%Z 0%Z) ∧ (∀(rl' : Z) (rh : Z), 0%Z ≤ rl' ∧ rl' < 0%Z -> rl' ≤ rh ∧ rh ≤ rows m -> (∀(cl : Z) (ch : Z), 0%Z ≤ cl ∧ cl ≤ ch ∧ ch ≤ columns m -> matrix_sum m rl' rh cl ch ≤ 0%Z))) ∧ (∀(chi : Z) (clo : Z) (rhi : Z) (rlo : Z) (maxsum : Z) (a1 : list Z), length a1 = length a -> (∀(rl : Z), (0%Z ≤ rl ∧ rl ≤ o2) ∧ (0%Z ≤ rlo ∧ rlo ≤ rhi ∧ rhi ≤ rows m) ∧ (0%Z ≤ clo ∧ clo ≤ chi ∧ chi ≤ columns m) ∧ (maxsum = matrix_sum m rlo rhi clo chi ∧ 0%Z ≤ matrix_sum m rlo rhi clo chi) ∧ (∀(rl' : Z) (rh : Z), 0%Z ≤ rl' ∧ rl' < rl -> rl' ≤ rh ∧ rh ≤ rows m -> (∀(cl : Z) (ch : Z), 0%Z ≤ cl ∧ cl ≤ ch ∧ ch ≤ columns m -> matrix_sum m rl' rh cl ch ≤ maxsum)) -> (let o3 : Z := columns m in (0%Z ≤ 0%Z ∧ 0%Z ≤ o3 ∧ 0%Z + o3 ≤ Z.of_nat (length a1)) ∧ (∀(a2 : list Z), length a2 = length a1 -> (∀(i : Z), 0%Z ≤ i ∧ i < 0%Z ∨ 0%Z + o3 ≤ i ∧ i < Z.of_nat (length a2) -> nth (Z.to_nat i) a2 inhabitant = nth (Z.to_nat i) a1 inhabitant) ∧ (∀(i : Z), 0%Z ≤ i ∧ i < 0%Z + o3 -> nth (Z.to_nat i) a2 inhabitant = 0%Z) -> (let o4 : Z := rows m in let o5 : Z := rl + 1%Z in (o5 ≤ o4 + 1%Z -> ((0%Z ≤ rlo ∧ rlo ≤ rhi ∧ rhi ≤ rows m) ∧ (0%Z ≤ clo ∧ clo ≤ chi ∧ chi ≤ columns m) ∧ (maxsum = matrix_sum m rlo rhi clo chi ∧ 0%Z ≤ matrix_sum m rlo rhi clo chi) ∧ (∀(rl' : Z) (rh' : Z), 0%Z ≤ rl' ∧ rl' ≤ rh' ∧ rh' ≤ rows m -> rl' < rl ∨ rl' = rl ∧ rh' < o5 -> (∀(cl : Z) (ch : Z), 0%Z ≤ cl ∧ cl ≤ ch ∧ ch ≤ columns m -> matrix_sum m rl' rh' cl ch ≤ maxsum)) ∧ (∀(j : Z), 0%Z ≤ j ∧ j < columns m -> nth (Z.to_nat j) a2 inhabitant = sum (col_closure m j) rl (o5 - 1%Z))) ∧ (∀(chi1 : Z) (clo1 : Z) (rhi1 : Z) (rlo1 : Z) (maxsum1 : Z) (a3 : list Z), length a3 = length a2 -> (∀(rh : Z), (o5 ≤ rh ∧ rh ≤ o4) ∧ (0%Z ≤ rlo1 ∧ rlo1 ≤ rhi1 ∧ rhi1 ≤ rows m) ∧ (0%Z ≤ clo1 ∧ clo1 ≤ chi1 ∧ chi1 ≤ columns m) ∧ (maxsum1 = matrix_sum m rlo1 rhi1 clo1 chi1 ∧ 0%Z ≤ matrix_sum m rlo1 rhi1 clo1 chi1) ∧ (∀(rl' : Z) (rh' : Z), 0%Z ≤ rl' ∧ rl' ≤ rh' ∧ rh' ≤ rows m -> rl' < rl ∨ rl' = rl ∧ rh' < rh -> (∀(cl : Z) (ch : Z), 0%Z ≤ cl ∧ cl ≤ ch ∧ ch ≤ columns m -> matrix_sum m rl' rh' cl ch ≤ maxsum1)) ∧ (∀(j : Z), 0%Z ≤ j ∧ j < columns m -> nth (Z.to_nat j) a3 inhabitant = sum (col_closure m j) rl (rh - 1%Z)) -> (let o6 : Z := columns m - 1%Z in (0%Z ≤ o6 + 1%Z -> ((∀(j : Z), 0%Z ≤ j ∧ j < 0%Z -> nth (Z.to_nat j) a3 inhabitant = sum (col_closure m j) rl rh) ∧ (∀(j : Z), 0%Z ≤ j ∧ j < columns m -> nth (Z.to_nat j) a3 inhabitant = sum (col_closure m j) rl (rh - 1%Z))) ∧ (∀(a4 : list Z), length a4 = length a3 -> (∀(c : Z), (0%Z ≤ c ∧ c ≤ o6) ∧ (∀(j : Z), 0%Z ≤ j ∧ j < c -> nth (Z.to_nat j) a4 inhabitant = sum (col_closure m j) rl rh) ∧ (∀(j : Z), c ≤ j ∧ j < columns m -> nth (Z.to_nat j) a4 inhabitant = sum (col_closure m j) rl (rh - 1%Z)) -> (let o7 : Z := rh - 1%Z in valid_index m o7 c ∧ (0%Z ≤ c ∧ c < Z.of_nat (length a4)) ∧ (let o8 : Z := nth (Z.to_nat c) a4 inhabitant + elts m o7 c in (0%Z ≤ c ∧ c < Z.of_nat (length a4)) ∧ (length (set_list a4 (Z.to_nat c) o8) = length a4 -> nth_i (set_list a4 (Z.to_nat c) o8) = fun_updt (nth_i a4) c o8 -> (∀(j : Z), 0%Z ≤ j ∧ j < c + 1%Z -> nth (Z.to_nat j) (set_list a4 (Z.to_nat c) o8) inhabitant = sum (col_closure m j) rl rh) ∧ (∀(j : Z), c + 1%Z ≤ j ∧ j < columns m -> nth (Z.to_nat j) (set_list a4 (Z.to_nat c) o8) inhabitant = sum (col_closure m j) rl (rh - 1%Z)))))) ∧ ((∀(j : Z), 0%Z ≤ j ∧ j < o6 + 1%Z -> nth (Z.to_nat j) a4 inhabitant = sum (col_closure m j) rl rh) ∧ (∀(j : Z), o6 + 1%Z ≤ j ∧ j < columns m -> nth (Z.to_nat j) a4 inhabitant = sum (col_closure m j) rl (rh - 1%Z)) -> (∀(lo : Z) (hi : Z), (0%Z ≤ lo ∧ lo ≤ hi ∧ hi ≤ Z.of_nat (length a4)) ∧ (∀(l : Z) (h : Z), 0%Z ≤ l ∧ l ≤ h ∧ h ≤ Z.of_nat (length a4) -> array_sum a4 l h ≤ array_sum a4 lo hi) -> (if decide (maxsum1 < array_sum a4 lo hi) then (0%Z ≤ rl ∧ rl ≤ rh ∧ rh ≤ rows m) ∧ (0%Z ≤ lo ∧ lo ≤ hi ∧ hi ≤ columns m) ∧ (array_sum a4 lo hi = matrix_sum m rl rh lo hi ∧ 0%Z ≤ matrix_sum m rl rh lo hi) ∧ (∀(rl' : Z) (rh' : Z), 0%Z ≤ rl' ∧ rl' ≤ rh' ∧ rh' ≤ rows m -> rl' < rl ∨ rl' = rl ∧ rh' < rh + 1%Z -> (∀(cl : Z) (ch : Z), 0%Z ≤ cl ∧ cl ≤ ch ∧ ch ≤ columns m -> matrix_sum m rl' rh' cl ch ≤ array_sum a4 lo hi)) ∧ (∀(j : Z), 0%Z ≤ j ∧ j < columns m -> nth (Z.to_nat j) a4 inhabitant = sum (col_closure m j) rl (rh + 1%Z - 1%Z)) else (0%Z ≤ rlo1 ∧ rlo1 ≤ rhi1 ∧ rhi1 ≤ rows m) ∧ (0%Z ≤ clo1 ∧ clo1 ≤ chi1 ∧ chi1 ≤ columns m) ∧ (maxsum1 = matrix_sum m rlo1 rhi1 clo1 chi1 ∧ 0%Z ≤ matrix_sum m rlo1 rhi1 clo1 chi1) ∧ (∀(rl' : Z) (rh' : Z), 0%Z ≤ rl' ∧ rl' ≤ rh' ∧ rh' ≤ rows m -> rl' < rl ∨ rl' = rl ∧ rh' < rh + 1%Z -> (∀(cl : Z) (ch : Z), 0%Z ≤ cl ∧ cl ≤ ch ∧ ch ≤ columns m -> matrix_sum m rl' rh' cl ch ≤ maxsum1)) ∧ (∀(j : Z), 0%Z ≤ j ∧ j < columns m -> nth (Z.to_nat j) a4 inhabitant = sum (col_closure m j) rl (rh + 1%Z - 1%Z))))))) ∧ (o6 + 1%Z < 0%Z -> (∀(lo : Z) (hi : Z), (0%Z ≤ lo ∧ lo ≤ hi ∧ hi ≤ Z.of_nat (length a3)) ∧ (∀(l : Z) (h : Z), 0%Z ≤ l ∧ l ≤ h ∧ h ≤ Z.of_nat (length a3) -> array_sum a3 l h ≤ array_sum a3 lo hi) -> (if decide (maxsum1 < array_sum a3 lo hi) then (0%Z ≤ rl ∧ rl ≤ rh ∧ rh ≤ rows m) ∧ (0%Z ≤ lo ∧ lo ≤ hi ∧ hi ≤ columns m) ∧ (array_sum a3 lo hi = matrix_sum m rl rh lo hi ∧ 0%Z ≤ matrix_sum m rl rh lo hi) ∧ (∀(rl' : Z) (rh' : Z), 0%Z ≤ rl' ∧ rl' ≤ rh' ∧ rh' ≤ rows m -> rl' < rl ∨ rl' = rl ∧ rh' < rh + 1%Z -> (∀(cl : Z) (ch : Z), 0%Z ≤ cl ∧ cl ≤ ch ∧ ch ≤ columns m -> matrix_sum m rl' rh' cl ch ≤ array_sum a3 lo hi)) ∧ (∀(j : Z), 0%Z ≤ j ∧ j < columns m -> nth (Z.to_nat j) a3 inhabitant = sum (col_closure m j) rl (rh + 1%Z - 1%Z)) else (0%Z ≤ rlo1 ∧ rlo1 ≤ rhi1 ∧ rhi1 ≤ rows m) ∧ (0%Z ≤ clo1 ∧ clo1 ≤ chi1 ∧ chi1 ≤ columns m) ∧ (maxsum1 = matrix_sum m rlo1 rhi1 clo1 chi1 ∧ 0%Z ≤ matrix_sum m rlo1 rhi1 clo1 chi1) ∧ (∀(rl' : Z) (rh' : Z), 0%Z ≤ rl' ∧ rl' ≤ rh' ∧ rh' ≤ rows m -> rl' < rl ∨ rl' = rl ∧ rh' < rh + 1%Z -> (∀(cl : Z) (ch : Z), 0%Z ≤ cl ∧ cl ≤ ch ∧ ch ≤ columns m -> matrix_sum m rl' rh' cl ch ≤ maxsum1)) ∧ (∀(j : Z), 0%Z ≤ j ∧ j < columns m -> nth (Z.to_nat j) a3 inhabitant = sum (col_closure m j) rl (rh + 1%Z - 1%Z))))))) ∧ ((0%Z ≤ rlo1 ∧ rlo1 ≤ rhi1 ∧ rhi1 ≤ rows m) ∧ (0%Z ≤ clo1 ∧ clo1 ≤ chi1 ∧ chi1 ≤ columns m) ∧ (maxsum1 = matrix_sum m rlo1 rhi1 clo1 chi1 ∧ 0%Z ≤ matrix_sum m rlo1 rhi1 clo1 chi1) ∧ (∀(rl' : Z) (rh' : Z), 0%Z ≤ rl' ∧ rl' ≤ rh' ∧ rh' ≤ rows m -> rl' < rl ∨ rl' = rl ∧ rh' < o4 + 1%Z -> (∀(cl : Z) (ch : Z), 0%Z ≤ cl ∧ cl ≤ ch ∧ ch ≤ columns m -> matrix_sum m rl' rh' cl ch ≤ maxsum1)) ∧ (∀(j : Z), 0%Z ≤ j ∧ j < columns m -> nth (Z.to_nat j) a3 inhabitant = sum (col_closure m j) rl (o4 + 1%Z - 1%Z)) -> (0%Z ≤ rlo1 ∧ rlo1 ≤ rhi1 ∧ rhi1 ≤ rows m) ∧ (0%Z ≤ clo1 ∧ clo1 ≤ chi1 ∧ chi1 ≤ columns m) ∧ (maxsum1 = matrix_sum m rlo1 rhi1 clo1 chi1 ∧ 0%Z ≤ matrix_sum m rlo1 rhi1 clo1 chi1) ∧ (∀(rl' : Z) (rh : Z), 0%Z ≤ rl' ∧ rl' < rl + 1%Z -> rl' ≤ rh ∧ rh ≤ rows m -> (∀(cl : Z) (ch : Z), 0%Z ≤ cl ∧ cl ≤ ch ∧ ch ≤ columns m -> matrix_sum m rl' rh cl ch ≤ maxsum1))))) ∧ (o4 + 1%Z < o5 -> (0%Z ≤ rlo ∧ rlo ≤ rhi ∧ rhi ≤ rows m) ∧ (0%Z ≤ clo ∧ clo ≤ chi ∧ chi ≤ columns m) ∧ (maxsum = matrix_sum m rlo rhi clo chi ∧ 0%Z ≤ matrix_sum m rlo rhi clo chi) ∧ (∀(rl' : Z) (rh : Z), 0%Z ≤ rl' ∧ rl' < rl + 1%Z -> rl' ≤ rh ∧ rh ≤ rows m -> (∀(cl : Z) (ch : Z), 0%Z ≤ cl ∧ cl ≤ ch ∧ ch ≤ columns m -> matrix_sum m rl' rh cl ch ≤ maxsum))))))) ∧ ((0%Z ≤ rlo ∧ rlo ≤ rhi ∧ rhi ≤ rows m) ∧ (0%Z ≤ clo ∧ clo ≤ chi ∧ chi ≤ columns m) ∧ (maxsum = matrix_sum m rlo rhi clo chi ∧ 0%Z ≤ matrix_sum m rlo rhi clo chi) ∧ (∀(rl' : Z) (rh : Z), 0%Z ≤ rl' ∧ rl' < o2 + 1%Z -> rl' ≤ rh ∧ rh ≤ rows m -> (∀(cl : Z) (ch : Z), 0%Z ≤ cl ∧ cl ≤ ch ∧ ch ≤ columns m -> matrix_sum m rl' rh cl ch ≤ maxsum)) -> (0%Z ≤ rlo ∧ rlo ≤ rhi ∧ rhi ≤ rows m) ∧ (0%Z ≤ clo ∧ clo ≤ chi ∧ chi ≤ columns m) ∧ maxsum = matrix_sum m rlo rhi clo chi ∧ (∀(rl : Z) (rh : Z), 0%Z ≤ rl ∧ rl ≤ rh ∧ rh ≤ rows m -> (∀(cl : Z) (ch : Z), 0%Z ≤ cl ∧ cl ≤ ch ∧ ch ≤ columns m -> matrix_sum m rl rh cl ch ≤ maxsum))))) ∧ (o2 + 1%Z < 0%Z -> (0%Z ≤ 0%Z ∧ 0%Z ≤ 0%Z ∧ 0%Z ≤ rows m) ∧ (0%Z ≤ 0%Z ∧ 0%Z ≤ 0%Z ∧ 0%Z ≤ columns m) ∧ 0%Z = matrix_sum m 0%Z 0%Z 0%Z 0%Z ∧ (∀(rl : Z) (rh : Z), 0%Z ≤ rl ∧ rl ≤ rh ∧ rh ≤ rows m -> (∀(cl : Z) (ch : Z), 0%Z ≤ cl ∧ cl ≤ ch ∧ ch ≤ columns m -> matrix_sum m rl rh cl ch ≤ 0%Z))))).
Admitted.
